import { Injectable } from '@angular/core';
import { ApiService } from '@core/services';
import { AccountsService } from '@features/accounts';

import { tap } from 'rxjs';

import { TransactionTypesEnum } from '../enums';
import { TransactionCategoryInterface, TransactionCreateInterface, TransactionReadInterface } from '../interfaces';

@Injectable({
  providedIn: 'root'
})
export class TransactionsService {
  constructor(
    private readonly apiService: ApiService,
    private readonly accountsService: AccountsService
  ) { }

  public getAll() {
    return this.apiService.get<TransactionReadInterface[]>('transactions');
  }

  public getCategories(typeId: TransactionTypesEnum) {
    return this.apiService.get<TransactionCategoryInterface[]>(`transactions/categories/${typeId}`);
  }

  public create(transaction: TransactionCreateInterface) {
    return this.apiService.post<TransactionCategoryInterface[]>('transactions', transaction)
      .pipe(tap(() => this.accountsService.update().subscribe()));
  }

  public getById(id: number) {

  }
}
