  selector: 'app-accounts',
  standalone: true,
  imports: [CommonModule],
  templateUrl: 'accounts.component.html',
  styleUrls: ['accounts.component.scss']
})
export class AccountsComponent {
  @Input() accounts: AccountInterface[] | null = null;

}
