import { Component, Inject, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';

import { Observable, takeUntil } from 'rxjs';

import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';

import { DestroyService } from '@core/services';
import { AccountsState } from '@features/accounts';
import { Loadable } from '@core/helpers';
import {
  TransactionCategoryInterface,
  TransactionCreateInterface,
  TransactionReadInterface,
  TransactionsService,
  TransactionTypesEnum
} from '@features/transactions';

@Component({
  selector: 'app-transaction-details',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,

    MatFormFieldModule,
    MatSelectModule,
    MatInputModule,
    MatButtonModule
  ],
  templateUrl: './transaction-details.component.html',
  styleUrls: ['./transaction-details.component.scss']
})
export class TransactionDetailsComponent {
  @Input() typeId: TransactionTypesEnum | null = null;
  @Input() title = 'Transaction';
  accounts$ = this.accountsState.data$;
  categories = new Loadable<TransactionCategoryInterface[]>([], false);
  transaction = new Loadable<TransactionReadInterface | null>(null, false);

  formGroup: FormGroup = this.formBuilder.group({
    amount: [null, Validators.required],
    description: [null, [Validators.required, Validators.maxLength(500)]],
    typeId: [TransactionTypesEnum.expense, Validators.required],
    categoryId: [null, [Validators.required]],
    accountId: [null, [Validators.required]]
  });

  constructor(
    @Inject(DestroyService) private readonly viewDestroyed$: Observable<void>,
    private readonly formBuilder: FormBuilder,
    private readonly transactionsService: TransactionsService,
    private readonly accountsState: AccountsState,
    private readonly router: Router,
    private readonly route: ActivatedRoute
  ) { }

  ngOnInit(): void {
    const expenseId = this.route.snapshot.paramMap.get('id');

    if (expenseId) {
      if (isNaN(Number(expenseId))) {
        this.router.navigate(['./']);
      } else {
        this.loadExpense(Number(expenseId));
      }
    }

    this.loadCategories();
  }

  onSubmit(form: any): void {
    console.log(form)
    if (this.transaction.data) {
      this.updateExpense(this.transaction.data.id, form);
    } else {
      this.createExpense(form);
    }
  }

  onNewClicked(): void {
    this.transaction.setData(null);
    this.formGroup.reset({ typeId: TransactionTypesEnum.expense });
  }

  onViewAllClicked(): void {
    this.router.navigate(['expenses']);
  }

  loadCategories(): void {
    this.categories.isLoading = true;
    this.transactionsService.getCategories(TransactionTypesEnum.expense)
      .pipe(takeUntil(this.viewDestroyed$))
      .subscribe({
        next: data => this.categories.setData(data),
        error: error => this.categories.setError(error)
      });
  }

  loadExpense(id: number): void {
    this.transaction.isLoading = true;
    this.transactionsService.getExpenseById(id)
      .pipe(takeUntil(this.viewDestroyed$))
      .subscribe(expense => {
        this.transaction.setData(expense);
        this.formGroup.reset(expense);
      });
  }

  createExpense(expense: TransactionCreateInterface): void {
    this.transaction.isLoading = true;
    this.transactionsService.create(expense)
      .pipe(takeUntil(this.viewDestroyed$))
      .subscribe(expense => {
        this.transaction.setData(expense);
        this.formGroup.reset(expense);
      });
  }

  updateExpense(id: number, expense: TransactionCreateInterface): void {
    this.transaction.isLoading = true;
    this.transactionsService.updateById(id, expense)
      .pipe(takeUntil(this.viewDestroyed$))
      .subscribe(expense => {
        this.transaction.setData(expense);
        this.formGroup.reset(expense);
      });
  }
}
