import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

import { MatToolbarModule } from '@angular/material/toolbar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';

import { AuthService, UserState } from '@features/users';

@Component({
  selector: 'app-toolbar',
  standalone: true,
  imports: [
    CommonModule,

    MatToolbarModule,
    MatIconModule,
    MatButtonModule
  ],
  template: `
    <p>
      <!-- <mat-toolbar color="primary" style="display: flex; justify-content: space-between;">
        <div>
          <button mat-icon-button (click)="onMenuClicked()">
            <mat-icon>menu</mat-icon>
          </button>        
          <button mat-icon-button (click)="onCloseClicked()">
            <mat-icon>close</mat-icon>
          </button>
          <span>FASystem</span>
        </div>
        <div *ngIf="(userData$ | async) as userData">
          {{userData.name}} - {{userData.login}}
        </div>
      </mat-toolbar> -->
      <mat-toolbar color="primary">
    <button mat-icon-button class="example-icon" aria-label="Example icon-button with menu icon">
      <mat-icon>menu</mat-icon>
    </button>
    <span>My App</span>
    <span class="example-spacer"></span>
    <button mat-icon-button class="example-icon favorite-icon" aria-label="Example icon-button with heart icon">
      <mat-icon>favorite</mat-icon>
    </button>
    <button mat-icon-button class="example-icon" aria-label="Example icon-button with share icon">
      <mat-icon>share</mat-icon>
    </button>
  </mat-toolbar>
    </p>
  `,
  styles: [
    `.spaicer: `
  ]
})
export class ToolbarComponent {
  public readonly userData$ = this.userState.data$;

  constructor(
    private readonly userState: UserState,
    private readonly authService: AuthService
  ) { }

  onMenuClicked() {
    this.authService.login({
      login: 'admin',
      password: '1234567890'
    }).subscribe();
  }

  onCloseClicked() {
    // this.userState.clear();
    this.authService.logout().subscribe();
  }
}
