import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlConfig, FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';

import { AuthService, LoginInterface, UserInterface } from '@features/users';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule
  ],
  templateUrl: 'login.component.html',
  styleUrls: [
    'login.component.scss'
  ]
})
export type ControlConfigMap<T> = {
  [K in keyof T]: ControlConfig<T[K]>
};

export class LoginComponent {
  formGroup: FormGroup = this.formBuilder.group<ControlConfigMap<LoginInterface>>({
    login: ['', Validators.required],
    password: ['', Validators.required]
  });

  constructor(
    private readonly authService: AuthService,
    private formBuilder: FormBuilder,
  ) { }
}
