import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlConfig, FormBuilder, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';

import { AuthService, LoginInterface } from '@features/users';
import { ControlConfigMap } from '@core/helpers';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule
  ],
  templateUrl: 'login.component.html',
  styleUrls: [
    'login.component.scss'
  ]
})
export class LoginComponent {
  formGroup: FormGroup = this.formBuilder.group<ControlConfigMap<LoginInterface>>({
    login: ['', Validators.required],
    password: ['', Validators.required]
  });

  constructor(
    private readonly authService: AuthService,
    private formBuilder: FormBuilder,,
    private readonly router: Router,
  ) { }

  onLoginSubmit(login: LoginInterface) {
    this.authService.login(login).subscribe(() => {

    });
  }
}
